mapper.update:
update tbl_activity
set owner = #{owner}, name = #{name}, startDate = #{startDate}, endDate = #{endDate}, cost = #{cost}, description = #{description},
    editTime = #{editTime}, editBy = #{editBy}
where id = #{id}

mapper.select:
select count(*) from tbl_activity as a join tbl_user as u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}

select a.id, u.name as owner, a.name, a.startDate, a.endDate
from tbl_activity as a
join tbl_user as u
on a.owner = u.id



        a.name like '%' #{name} '%'



        and u.name like '%' #{owner} '%'



        and a.startDate > #{startDate}



        and a.endDate < #{endDate}




order by a.createTime desc

limit #{skipCount}, #{pageSize}

select ca.id as id, a.name, a.startDate, a.endDate, u.name as owner
from tbl_activity as a join tbl_user as u on a.owner = u.id join tbl_clue_activity_relation as ca on a.id = ca.activityId
where ca.clueId = #{clueId}

select a.id, a.name, a.startDate, a.endDate, u.name as owner
from tbl_activity as a join tbl_user as u on a.owner=u.id
where a.name like '%' #{aname} '%' and a.id not in (
select activityId from tbl_clue_activity_relation where clueId=#{clueId}
)
