mapper.select:
select count(*)
from tbl_tran t join tbl_customer cus on t.customerId=cus.id join tbl_user u on t.owner=u.id join tbl_contacts con on t.contactsId=con.id
u.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and cus.name like '%' #{customerName} '%'
and con.fullname like '%' #{contactsName} '%'
and t.stage=#{stage}
and t.type=#{type}
and t.source=#{source}

select t.id, u.name as owner, t.money, t.name, t.expectedDate, cus.name as customerId, t.stage, t.type, t.source, t.activityId, con.fullname as contactsId, t.createBy, t.createTime, t.editBy, t.editTime, t.description, t.contactSummary, t.nextContactTime
from tbl_tran t join tbl_customer cus on t.customerId=cus.id join tbl_user u on t.owner=u.id join tbl_contacts con on t.contactsId=con.id
u.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and cus.name like '%' #{customerName} '%'
and con.fullname like '%' #{contactsName} '%'
and t.stage=#{stage}
and t.type=#{type}
and t.source=#{source}
order by t.createTime desc limit #{skipCount}, #{pageSize}

mapper.insert:
insert into tbl_tran(
id, owner, money, name, expectedDate, customerId, stage, type, source, activityId, contactsId, createBy, createTime, description, contactSummary, nextContactTime
)values(
#{id}, #{owner}, #{money}, #{name}, #{expectedDate}, #{customerId}, #{stage}, #{type}, #{source}, #{activityId}, #{contactsId}, #{createBy}, #{createTime}, #{description}, #{contactSummary}, #{nextContactTime}
)
